import React, {Component} from 'react';
import {Table, Button} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import axios from 'axios';

class Products extends Component{
	state = {
	    pools : [],
	    pool_info: "",
	    pool_type: "",
	    store_id: "",
	}

	componentDidMount() {
	    let data;

	    axios.get('http://localhost:8000/dropshipping/product-pool')
	    .then(res => {
	      data = res.data;
	      this.setState({
	        pools : data  
	      });
	    })
	    .catch(err => {})
	}

	render(){
		return 	<>
					<Button className="my-3 pull-right">  
						<Icon.ArrowRight />
					</Button>
					<Table striped bordered hover responsive>
					  <thead>
					    <tr>
					      <th>#</th>
					      <th>Store</th>
					      <th>Pool Type</th>
					      <th>Pool Info</th>
					    </tr>
					  </thead>
					  <tbody>
					  	{this.state.pools.map((pool, id) => (
						    <tr>
						      <td>{id+1}</td>
						      <td>{ pool.store_name }</td>
						      <td>{ pool.pool_type }</td>
						      <td>{ pool.pool_info }</td>
						    </tr>
						    )
      					)}
					  </tbody>
					</Table>
				</>;
	}
}

export default Products;
