import React, {Component} from 'react';
import {Table, Button, Form, Row, Col, FormControl} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import FloatingLabel from "react-bootstrap-floating-label";
import $ from 'jquery';

class ProductSearchPool extends Component{

	constructor(props) {
        super(props);
        this.reset_form = this.reset_form.bind(this);
    }

    state = {
	    pools : [],
	}

	componentDidMount() {
	    let data;
	    console.log(this.props.configs)
	    this.props.axios.get('dropshipping/product-pool')
	    .then(res => {
	      data = res.data;
	      this.setState({
	        pools : data  
	      });
	    })
	    .catch(err => {})
	}

	handleSubmit = (e) => {
        e.preventDefault();

  		var data = new FormData($('#product-pool-form')[0])

        this.props.axios
            .post('dropshipping/product-pool', data)
            .then((res) => {
            	this.props.toast.info(res.msg);
                this.reset_form($('#product-pool-form'));
            })
            .catch((err) => {

            	let error_txt = "";

            	if(err.response.data)
            	{
            		$.each(err.response.data, function(index, value){
            			error_txt += index+' : '+value+'\n';
            		});
				}

            	if(error_txt.length > 0)
            	{
            		this.props.toast.dark(error_txt);
            	}	
            	else
            	{
            		this.props.toast.dark('Something Went Wrong');
            	}
            });
    };

    reset_form = (el) =>{
    	$(el).find('input, select, textarea').each(function(){
    		$(this).val('');
    		$(this).html('');
    	});
    }

	render(){
		return 	<>
					<Form onSubmit={this.handleSubmit} id="product-pool-form">
						<Row>
							<Col>
								<div class="form-floating">
								  <select type="password" class="form-control" id="floatingPassword" placeholder="Password">
								  		<option value="option1">Option1</option>
								  </select>
								  <label for="floatingPassword">Password</label>
								</div>
								<FloatingLabel label="Store" className="mb-3" inputName="store_id">
								    <Form.Select>
								    	<option value="option">option</option>
								    	if (this.props.configs.stores) {
								    		this.props.configs.stores.map((value) =>(
							    			    <option value="{value.id}">{value.store_name}</option>
									    		)
									    	)
								    	}
								    </Form.Select>
								</FloatingLabel>
							</Col>
							<Col>
								<FloatingLabel controlId="floatingPoolType" label="Pool Type" inputName="pool_type">
								    <Form.Control placeholder="Pool Type" as="select"/>
								</FloatingLabel>
							</Col>
							<Col>
								<FloatingLabel controlId="floatingPoolInfo" label="Pool Info" inputName="pool_info">
								    <Form.Control as="textarea" placeholder="Pool Info"/>
								</FloatingLabel>
							</Col>
							<Col>
								<Button type="submit">  
									<Icon.Save />
								</Button>
							</Col>
						</Row>
					</Form>

					<Table striped bordered hover responsive>
					  <thead>
					    <tr>
					      <th>Store</th>
					      <th>Pool Type</th>
					      <th>Pool Info</th>
					    </tr>
					  </thead>
					  <tbody>
					  	{this.state.pools.map((pool, id) => (
						    <tr>
						      <td>{ pool.store_name }</td>
						      <td>{ pool.pool_type }</td>
						      <td>{ pool.pool_info }</td>
						    </tr>
						    )
      					)}
					  </tbody>
					</Table>
				</>;
	}
}


export default ProductSearchPool;
