import React, {Component} from 'react';
import {Table, Button, Form, Row, Col, FormControl} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import axios from 'axios';
import * as config from './../config';
import FloatingLabel from "react-bootstrap-floating-label";


class ProductSearchPool extends Component{

	state = {
	    pools : [],
	    pool_info: "",
	    pool_type: "",
	    store_id: "",
	}

	componentDidMount() {
	    let data;

	    axios.defaults.baseURL = config.ALIAS.apiUrl;

	    axios.get('dropshipping/product-pool')
	    .then(res => {
	      data = res.data;
	      this.setState({
	        pools : data  
	      });
	    })
	    .catch(err => {})
	}

	handleSubmit = (e) => {
        e.preventDefault();

        axios
            .post(e.target.action, {
                pool_info: this.state.pool_info,
                pool_type: this.state.pool_type,
                store_id: this.state.store_id,
            })
            .then((res) => {
                this.setState({
                    pool_info: "",
                    pool_type: "",
                    store_id: "",
                });
            })
            .catch((err) => {});
    };

	render(){
		return 	<>
					<Form onSubmit={this.handleSubmit} action="/dropshipping/product-pool">
						<Row>
							<Col>
								<FloatingLabel  controlId="floatingStore" label="Store" className="mb-3">
								    <Form.Control name="store_id" type="text" placeholder="Store"/>
								</FloatingLabel>
							</Col>
							<Col>
								<FloatingLabel controlId="floatingPoolType" label="Pool Type">
								    <Form.Control type="text" placeholder="Pool Type" name="pool_type"/>
								</FloatingLabel>
							</Col>
							<Col>
								<FloatingLabel controlId="floatingPoolInfo" label="Pool Info">
								    <Form.Control as="textarea" placeholder="Pool Info" name="pool_info"/>
								</FloatingLabel>
							</Col>
							<Col>
								<Button type="submit">  
									<Icon.Save />
								</Button>
							</Col>
						</Row>
					</Form>

					<Table striped bordered hover responsive>
					  <thead>
					    <tr>
					      <th>Store</th>
					      <th>Pool Type</th>
					      <th>Pool Info</th>
					    </tr>
					  </thead>
					  <tbody>
					  	{this.state.pools.map((pool, id) => (
						    <tr>
						      <td>{ pool.store_name }</td>
						      <td>{ pool.pool_type }</td>
						      <td>{ pool.pool_info }</td>
						    </tr>
						    )
      					)}
					  </tbody>
					</Table>
				</>;
	}
}

export default ProductSearchPool;
