import React from 'react';
import axios from 'axios';

class App extends React.Component {

  state = {
    details : [],
  }

  componentDidMount() {

    let data ;

    axios.get('http://localhost:8000/product-pool/')
    .then(res => {
      data = res.data;
      this.setState({
        details : data  
      });
    })
    .catch(err => {})
  }

render() {
  return(
  <div>
      {this.state.details.map((detail, id) => (
      <div key={id}>
      <div >
        <div >
            <h1>{detail.detail} </h1>
            <footer >--- by
            <cite title="Source Title">
            {detail.name}</cite>
            </footer>
        </div>
      </div>
      </div>
      )
    )}
  </div>
  );
}
}

export default App;
