import React from 'react';
import axios from 'axios';

class App2 extends React.Component {

  state = {
    pools : [],
    pool_info: "",
    pool_type: "",
    store_id: "",
  }

  componentDidMount() {

    let data;

    axios.get('http://localhost:8000/dropshipping/product-pool')
    .then(res => {
      data = res.data;
      this.setState({
        pools : data  
      });
    })
    .catch(err => {})
  }

  renderSwitch = (param) => {
        switch (param + 1) {
            case 1:
                return "primary ";
            case 2:
                return "secondary";
            case 3:
                return "success";
            case 4:
                return "danger";
            case 5:
                return "warning";
            case 6:
                return "info";
            default:
                return "yellow";
        }
    };

    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();

        axios
            .post("http://localhost:8000/dropshipping/product-pool", {
                pool_info: this.state.pool_info,
                pool_type: this.state.pool_type,
                store_id: this.state.store_id,
            })
            .then((res) => {
                this.setState({
                    pool_info: "",
                    pool_type: "",
                    store_id: "",
                });
            })
            .catch((err) => {});
    };


render() {
  return(
      <div className="container jumbotron ">
          <form onSubmit={this.handleSubmit}>
              <div className="input-group mb-3">
                  <div className="input-group-prepend">
                      <span className="input-group-text"
                            id="basic-addon1">
                          {" "}
                          Store{" "}
                      </span>
                  </div>
                  <input type="text" className="form-control" 
                         placeholder=""
                         aria-label="Username"
                         aria-describedby="basic-addon1"
                         value={this.state.store_id} name="store_id"
                         onChange={this.handleInput} />
              </div>

              <div className="input-group mb-3">
                  <div className="input-group-prepend">
                      <span className="input-group-text">
                         Pool Type 
                      </span>
                  </div>
                  <input type="text" className="form-control" 
                         placeholder=""
                         aria-label="Username"
                         aria-describedby="basic-addon1"
                         value={this.state.pool_type} name="pool_type"
                         onChange={this.handleInput} />
              </div>

              <div className="input-group mb-3">
                  <div className="input-group-prepend">
                      <span className="input-group-text">
                         Pool Info 
                      </span>
                  </div>       
                  <textarea className="form-control " 
                            aria-label="With textarea"
                            placeholder="" 
                            value={this.state.pool_info} name="pool_info" 
                            onChange={this.handleInput}>
                  </textarea>
              </div>

              <button type="submit" className="btn btn-primary mb-5">
                  Submit
              </button>
          </form>

          <hr
              style={{
                  color: "#000000",
                  backgroundColor: "#000000",
                  height: 0.5,
                  borderColor: "#000000",
              }}
          />
        {this.state.pools.map((pool, id) => (
        <div key={id}>
            <div className="card shadow-lg">
                <div className={"bg-" + this.renderSwitch(id % 6) + " card-header"}>Pool Type {id + 1}</div>
                <div className="card-body">
                    <blockquote className={"text-" + this.renderSwitch(id % 6) + 
                                       " blockquote mb-0"}>
                        <h1> {pool.pool_type} </h1>
                        <footer className="blockquote-footer">
                            {" "}
                            <cite title="Source Title">{pool.pool_info}</cite>
                        </footer>
                    </blockquote>
                </div>
            </div>
            <span className="border border-primary "></span>
        </div>
        )
      )}
    </div>
  );
}
}

export default App;
